import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  TextInput,
  Button,
  SafeAreaView,
} from "react-native";
import { Formik, Form, Field } from "formik";

import { useDispatch } from "react-redux";

import { createUser } from "../store/actions/userActions";
const Register = ({ id, email, password }) => {
  const dispatch = useDispatch();
  const history = useHistory();

  return (
    <View>
      <Formik
        initialValues={{
          email,
          password,
        }}
        onSubmit={async (values, { setSubmitting, resetForm }) => {
          dispatch(createUser(values)).then((response) => {
            resetForm();
            setSubmitting(false);
          });
        }}
      >
        {({ isSubmitting }) => (
          <Form className="categoryForm__form">
            <Row>
              <Button
                className="btn btn-light text-secondary btn-sm float-left"
                onClick={() => history.push("/admin/categories")}
              >
                <ArrowLeftCircle size={20} />
              </Button>
            </Row>
            <Container className="my-5 d-flex justify-content-center">
              <h2 className="text-center">Categorias</h2>
            </Container>
            <hr className="mt-0 mb-3" />

            <Row className="mt-5">
              <Col>
                <CustomInput
                  label="Nombre"
                  name="name"
                  type="text"
                  placeholder="Nombre de categoria"
                />
              </Col>
              <Col>
                <CustomInput
                  label="Descripcion"
                  name="description"
                  type="text"
                />
              </Col>
            </Row>

            <Button
              block
              className="bg-color-primary shadow-primary rounded-pill border-0 mt-5"
              type="submit"
            >
              {isSubmitting
                ? "Cargando..."
                : action === "put"
                ? "Actualizar categoria"
                : action === "delete"
                ? "Eliminar categoria"
                : action === "post"
                ? "Agregar categoria"
                : null}
            </Button>
          </Form>
        )}
      </Formik>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 10,
  },
  scrollView: {
    backgroundColor: "white",
    marginHorizontal: 20,
  },
  text: {
    fontSize: 42,
  },
});

function mapStateToProps(state) {
  return {
    userP: state.users,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    createUSerP: (user) => dispatch(createUser(user)),
  };
}

export default Register;
